Bash(/Users/maxix/Workspace/boom/user-logs/user-log-lib/build.gradle.kts << 'EOF'

jreleaser {
	// Подпись артефактов
	signing {
		active.set\(org.jreleaser.model.Active.ALWAYS\)
		armored.set\(true\)
	}

	// Настройка деплоя
	deploy {
		maven {
			// Maven Central через Portal API
			mavenCentral {
				create\("sonatype"\) {
					active.set\(org.jreleaser.model.Active.RELEASE\)
					url.set\("https://central.sonatype.com/api/v1/publisher"\)
					stagingRepository\("build/staging-deploy"\)
					setApplyMavenCentralRules\(true\)
				}
			}

			// GitHub Packages
			github {
				create\("github-packages"\) {
					active.set\(org.jreleaser.model.Active.RELEASE\)
					url.set\("https://maven.pkg.github.com/maxixcom/audit-event"\)
					stagingRepository\("build/staging-deploy"\)
				}
			}
		}
	}
}
EOF)
